mapper.insert:
INSERT INTO article
	SET regDate = NOW()
		, updateDate = NOW()
		, memberId = #{memberId}
		, boardId = #{boardId}
		, title = #{title}
		, `body` = #{body}

mapper.select:
SELECT AMB.* , IFNULL(SUM(RP.point), 0) AS sumReactionPoint FROM ( SELECT A.* , M.nickname AS writerNickname , B.name AS boardName , IFNULL(LAG(A.id) OVER(ORDER BY A.id), 0) AS previousArticleId , IFNULL(LEAD(A.id) OVER(ORDER BY A.id), 0) AS nextArticleId FROM article AS A INNER JOIN `member` AS M ON A.memberId = M.id INNER JOIN board AS B ON A.boardId = B.id WHERE 1 = 1 AND B.type = #{boardType}
AND A.boardId = #{boardId}
AND A.title LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.body LIKE CONCAT('%', #{searchKeyword}, '%')
AND M.nickname LIKE CONCAT('%', #{searchKeyword}, '%')
AND ( A.title LIKE CONCAT('%', #{searchKeyword}, '%') OR A.body LIKE CONCAT('%', #{searchKeyword}, '%') )
AND A.memberId = #{memberId}
ORDER BY A.id DESC ) AS AMB LEFT JOIN reactionPoint AS RP ON RP.relTypeCode = 'article' AND RP.relId = AMB.id WHERE AMB.id = #{articleId} GROUP BY AMB.id

mapper.update:
UPDATE article
	SET updateDate = NOW()
		, boardId = #{boardId}
		, title = #{title}
		, `body` = #{body}
	WHERE id = #{articleId}

UPDATE article
	SET viewCount = viewCount + 1
	WHERE id = #{articleId}

mapper.select:
SELECT COUNT(*) FROM article AS A INNER JOIN board AS B ON A.boardId = B.id INNER JOIN `member` AS M ON A.memberId = M.id WHERE 1 = 1 AND B.type = #{boardType}
AND A.title LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.body LIKE CONCAT('%', #{searchKeyword}, '%')
AND M.nickname LIKE CONCAT('%', #{searchKeyword}, '%')
AND ( A.title LIKE CONCAT('%', #{searchKeyword}, '%') OR A.body LIKE CONCAT('%', #{searchKeyword}, '%') )
AND A.memberId = #{memberId}

WITH AMB AS ( SELECT A.* , M.nickname AS writerNickname , B.name AS boardName FROM article AS A INNER JOIN `member` AS M ON A.memberId = M.id INNER JOIN board AS B ON A.boardId = B.id WHERE 1 = 1 AND B.type = #{boardType}
AND A.title LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.body LIKE CONCAT('%', #{searchKeyword}, '%')
AND M.nickname LIKE CONCAT('%', #{searchKeyword}, '%')
AND ( A.title LIKE CONCAT('%', #{searchKeyword}, '%') OR A.body LIKE CONCAT('%', #{searchKeyword}, '%') )
AND A.memberId = #{memberId}
) SELECT AMB.* , IFNULL(SUM(RP.point), 0) AS sumReactionPoint FROM AMB LEFT JOIN reactionPoint AS RP ON RP.relTypeCode = 'article' AND RP.relId = AMB.id GROUP BY AMB.id ORDER BY AMB.id DESC LIMIT #{limitStart}, #{itemsInAPage}

WITH AMB AS ( SELECT A.* , M.nickname AS writerNickname , B.name AS boardName FROM article AS A INNER JOIN `member` AS M ON A.memberId = M.id INNER JOIN board AS B ON A.boardId = B.id WHERE 1 = 1 AND A.boardId = #{boardId}
AND A.title LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.body LIKE CONCAT('%', #{searchKeyword}, '%')
AND M.nickname LIKE CONCAT('%', #{searchKeyword}, '%')
AND ( A.title LIKE CONCAT('%', #{searchKeyword}, '%') OR A.body LIKE CONCAT('%', #{searchKeyword}, '%') )
) SELECT AMB.* , IFNULL(SUM(RP.point), 0) AS sumReactionPoint FROM AMB LEFT JOIN reactionPoint AS RP ON RP.relTypeCode = 'article' AND RP.relId = AMB.id GROUP BY AMB.id ORDER BY AMB.id DESC LIMIT #{limitStart}, #{itemsInAPage}
