mapper.insert:
INSERT INTO `member`
	SET regDate = NOW()
		, updateDate = NOW()
		, loginId = #{loginId}
		, loginPw = #{loginPw}
		, `name` = #{name}
		, nickname = #{nickname}
		, birthday = #{birthday}
		, gender = #{gender}
		, email = #{email}
		, cellphoneNum = #{cellphoneNum}

mapper.update:
UPDATE `member`
	SET updateDate = NOW()
		, nickname = #{nickname}
		, email = #{email}
		, cellphoneNum = #{cellphoneNum}
	WHERE id = #{loginedMemberId}

UPDATE `member`
	SET updateDate = NOW()
		, loginPw = #{newLoginPw}
	WHERE id = #{loginedMemberId}
